package zakadabar.stack.frontend.resources

import kotlin.properties.ReadOnlyProperty
import kotlin.reflect.KProperty

val iconSources = mutableMapOf<String, ZkIconSource>() // key is property name

class IconSourceDelegate(private var source: ZkIconSource) : ReadOnlyProperty<Any?, ZkIconSource> {
    override fun getValue(thisRef: Any?, property: KProperty<*>) = source
}

class IconSourceDelegateProvider(val source: String) {

    operator fun provideDelegate(thisRef: Any?, prop: KProperty<*>): ReadOnlyProperty<Any?, ZkIconSource> {
        val iconSource = ZkIconSource(prop.name, source)
        iconSources[prop.name] = iconSource
        return IconSourceDelegate(iconSource)
    }
}

fun iconSource(source: String) = IconSourceDelegateProvider(source)

/**
 * Built-in icons. These are typically Material Icons from material.io. To add one
 *
 * * go to https://material.io/resources/icons
 * * select an icon
 * * on the bottom left corner select download SVG 24
 * * open downloaded the file in a text editor
 * * copy the part **BETWEEN** the "svg" tags into an IconSource constructor parameter
 *
 * By convention the name of the field is the name of the material icon.
 *
 * On the long run icons should be downloaded from the server as part of the user
 * theme.
 */
